// Extract quantiles for visualization
const quantiles = $json.forecast.quantiles[0]; // First batch
const q5 = quantiles.map(q => q[0]);   // 5th percentile (lower bound)
const q25 = quantiles.map(q => q[1]);  // 25th percentile
const q50 = quantiles.map(q => q[2]);  // 50th percentile (median)
const q75 = quantiles.map(q => q[3]);  // 75th percentile
const q95 = quantiles.map(q => q[4]);  // 95th percentile (upper bound)

return [{
  "median": q50,
  "lowerBound": q5,
  "upperBound": q95,
  "q25": q25,
  "q75": q75,
  "metadata": $json.metadata
}];

